import React from 'react';
// import useEscapeClose from '../hooks/useEscapeClose.js';
import Accept_button from '../images/Accept_button.png';
import Error_button from '../images/Error_button.png';

///// Попап accept/reject регистрации
function InfoTooltip({ onClose, onOverlayClose, result: { isOpen, ok } }) {

  return (
    <section className={`popup popup_type_info ${isOpen ? 'popup_opened' : false}`} onClick={onOverlayClose}>
      <div className="popup__container popup__container_type_info">
        <img className="popup__photo popup__photo_type_info" src={ok ? Accept_button : Error_button} alt='Результат действия' />
        <h2 className={`popup__title popup__title_type_info`}>{ok ? 'Вы успешно зарегистрировались!' : 'Что-то пошло не так! Попробуйте еще раз.'}</h2>
        <button onClick={onClose} className="popup__close" type="button" aria-label="Закрыть окно" />
      </div>
    </section>
  )
}

export default InfoTooltip;
