import React from "react";
import "../index.css";
import Header from "./Header.js";
import Main from "./Main.js";
import Footer from "./Footer.js";
import PopupWithForm from "./PopupWithForm.js";
import ImagePopup from "./ImagePopup.js";

function App() {
  const [isEditProfilePopupOpen, setEditProfileClick] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);


  function closeAllPopups() {
    setEditProfileClick(false);
    setAddPlacePopupOpen(false);
    setEditAvatarPopupOpen(false);
  }


  return (
    <>
      <div className="page">
        <Header />
        <Main
          onEditProfile={() => setEditProfileClick(true)}
          onEditAvatar={() => setEditAvatarPopupOpen(true)}
          onAddPlace={() => setAddPlacePopupOpen(true)}
        />
        <Footer />
        <PopupWithForm 
          name="profile" 
          title="Редактировать профиль" 
          onClose={closeAllPopups} 
          isOpen={isEditProfilePopupOpen}
          buttonTitle="Сохранить">
            <input type="text" name="name" value="" placeholder="Имя" className="popup__input popup__input_field_name" 
            id="name-input" required minLength="2" maxLength="40" />
            <span className="name-input-error popup__input-error"></span>
            <input type="text" name="about" value="" placeholder="Информация" className="popup__input popup__input_field_info" 
            id="info-input" required minLength="2" maxLength="200" />
            <span className="info-input-error popup__input-error popup__input-error_centrate"></span>
        </PopupWithForm>


        <PopupWithForm 
          name="cards" 
          title="Новое место" 
          onClose={closeAllPopups} 
          isOpen={isAddPlacePopupOpen}
          buttonTitle="Сохранить">
            <input type="text" placeholder="Название" className="popup__input popup__input_card_name" 
            id="cards-name" name="name" required minLength="2" maxLength="30" />
            <span className="cards-name-error popup__input-error"></span>
            <input type="url" name="link" placeholder="Ссылка на картинку" className="popup__input popup__input_card_link" 
            id="cards-link" required />
            <span className="cards-link-error popup__input-error popup__input-error_centrate"></span>
        </PopupWithForm>
        <PopupWithForm 
          name="avatar" 
          title="Обновить аватар" 
          onClose={closeAllPopups} 
          isOpen={isEditAvatarPopupOpen}
          buttonTitle="Сохранить">
            <input type="url" name="link" placeholder="Ссылка на картинку" className="popup__input popup__input_avatar_link" id="avatar-link" required />
            <span className="avatar-link-error popup__input-error popup__input-error_centrate"></span>
        </PopupWithForm>


        <ImagePopup />






      </div>
    </>
  );
}

export default App;
