import React, {useState} from 'react';
import { Link } from 'react-router-dom';

//--- Компонент регистрации пользователя ---
function Register({ onRegister }) {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  //---ОБРАБОТЧИКИ---
  function handleChangeEmail(e) {
    setEmail(e.target.value);
  }

  function handleChangePassword(e) {
    setPassword(e.target.value);
  }

  function handleSubmit(e) {
    e.preventDefault();
    onRegister(password, email);
  }


  //---РАЗМЕТКА JSX---
  return (
    <section className='registration             page__center' >
      <div className='registration__container'>
        <h3 className='registration__title'>Регистрация</h3>
        <form className='registration__form' name='registration-form-register' noValidate onSubmit={handleSubmit}>
          <input
            value={email}
            onChange={handleChangeEmail}
            id='loggin-input'
            type='email'
            placeholder='Email'
            className='registration__input'
            name='loggin'
            minLength='2'
            maxLength='40'
            required
          />


          <input
            value={password}
            onChange={handleChangePassword}
            id="password-input"
            type="password"
            placeholder="Пароль"
            className="registration__input"
            name="password"
            minLength="4"
            maxLength="10"
            required
          />

          <button className='registration__submit-button' type="submit">Зарегистрироваться</button>
          <Link to='sign-in' className='registration__link'>Уже зарегестрированы? Войти</Link>
        </form>
      </div>
    </section>
  )
}

export default Register;
