import React, { useState, useEffect } from "react";
import Header from "./Header.js";
import Main from "./Main.js";
import Footer from "./Footer.js";
import PopupWithForm from "./PopupWithForm.js";
import ImagePopup from "./ImagePopup.js";
import api from "../utils/Api.js";
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";

function App() {
  const [isEditProfilePopupOpen, setEditProfileClick] = useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = useState(false);
  const [isEditAcceptPopupOpen, setEditAcceptPopupOpen] = useState(false);

  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);
  const [selectedCard, setSelectedCard] = useState({
    isOpen: false,
    element: {},
  });

  useEffect(() => {
    api
      .getProfileData()
      .then((data) => {
        setCurrentUser(data);
      })
      .catch((err) => console.log(err));
  }, []);

  useEffect(() => {
    api
      .getInitialCards()
      .then((data) => {
        setCards(data);
      })
      .catch((err) => console.log(err));
  }, []);

  function closeAllPopups() {
    setEditProfileClick(false);
    setAddPlacePopupOpen(false);
    setEditAvatarPopupOpen(false);
    setEditAcceptPopupOpen(false);
    setSelectedCard({ isOpen: false, element: {} });
  }

  function handleCardClick(card) {
    setSelectedCard({ isOpen: true, element: card });
  }

  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i._id === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked)
    .then((newCard) => {
        setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    })
    .catch(err => {
      console.log(err);
    });
  }








  console.log(selectedCard);
  return (
    <CurrentUserContext.Provider value={currentUser}>
    <div className="page">
      <Header />
      <Main
        currentUser={currentUser}
        cards={cards}
        onEditProfile={() => setEditProfileClick(true)}
        onEditAvatar={() => setEditAvatarPopupOpen(true)}
        onAddPlace={() => setAddPlacePopupOpen(true)}
        onCardClick={handleCardClick}
        onCardLike={handleCardLike}
      />
      <Footer />

      <ImagePopup card={selectedCard} onClose={closeAllPopups} />

      <PopupWithForm
        name="profile"
        title="Редактировать профиль"
        onClose={closeAllPopups}
        isOpen={isEditProfilePopupOpen}
        // Добавить value инпутам в след проекте(для валидации)
        buttonTitle="Сохранить"
      >
        <input
          type="text"
          name="name"
          placeholder="Имя"
          className="popup__input popup__input_field_name"
          id="name-input"
          required
          minLength="2"
          maxLength="40"
        />
        <span className="name-input-error popup__input-error"></span>
        <input
          type="text"
          name="about"
          placeholder="Информация"
          className="popup__input popup__input_field_info"
          id="info-input"
          required
          minLength="2"
          maxLength="200"
        />
        <span className="info-input-error popup__input-error popup__input-error_centrate"></span>
      </PopupWithForm>

      <PopupWithForm
        name="cards"
        title="Новое место"
        onClose={closeAllPopups}
        isOpen={isAddPlacePopupOpen}
        buttonTitle="Сохранить"
      >
        <input
          type="text"
          placeholder="Название"
          className="popup__input popup__input_card_name"
          id="cards-name"
          name="name"
          required
          minLength="2"
          maxLength="30"
        />
        <span className="cards-name-error popup__input-error"></span>
        <input
          type="url"
          name="link"
          placeholder="Ссылка на картинку"
          className="popup__input popup__input_card_link"
          id="cards-link"
          required
        />
        <span className="cards-link-error popup__input-error popup__input-error_centrate"></span>
      </PopupWithForm>

      <PopupWithForm
        name="avatar"
        title="Обновить аватар"
        onClose={closeAllPopups}
        isOpen={isEditAvatarPopupOpen}
        buttonTitle="Сохранить"
      >
        <input
          type="url"
          name="link"
          placeholder="Ссылка на картинку"
          className="popup__input popup__input_avatar_link"
          id="avatar-link"
          required
        />
        <span className="avatar-link-error popup__input-error popup__input-error_centrate"></span>
      </PopupWithForm>

      <PopupWithForm
        name="accept"
        title="Вы уверены?"
        onClose={closeAllPopups}
        isOpen={isEditAcceptPopupOpen}
        buttonTitle="Да"
      ></PopupWithForm>
    </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
