import React from 'react'

function ImagePopup({ name, title, link, close, isOpen }) {
  return (
    <div className={`popup popup_type_${name} ${isOpen ? 'popup_opened' : false}`}>
      <div className={`popup__container popup__container_type_${name}`}>
        <button
          onClick={close}
          className="popup__close" type="button">
        </button>
        <img className="popup__image" src={link} alt="Картинка" />
        <h2 className={`popup__title popup__title_type_${name}`}>{title}</h2>
      </div>
    </div>
  );
}

export default ImagePopup;
