//     authorization: "0cd386ae-1830-42e7-aefa-1c5dfe1b78a1",
//     "Content-Type": "application/json",
//   },
// });

const api = new Api({
  baseUrl: BASE_URL,
  headers: {
    authorization: `Bearer ${localStorage.getItem('token')}`,
    'Content-Type': 'application/json'
  },
});

export default api;
