import React from "react";

function Card(props) {

  function handleClick() {
   props.onCardClick(props.card);
  }  

  // console.log(props.onCardClick(props.card))

  return(
      <li className="element">
        <button className="element__delete-card" type="button"></button>
        <img className="element__photo" src={props.card.link} onClick={handleClick} alt="Картинка" />
        <div className="element__title">
          <h2 className="element__text">{props.card.name}</h2>
          <div className="element__group">
            <button className="element__like" type="button"></button>
            <p className="element__sum">{props.card.likes.length}</p>
          </div>
        </div>
      </li>
  );
}

export default Card;

