import React from "react";

function PopupWithForm({ name, title, children, onClose, isOpen, submit, buttonTitle, onSubmit }) {
  return (
      <div className={`popup popup_type_${name} ${isOpen ? 'popup_opened' : false}`}>
        <div className={`popup__container popup__container_type_${name}`}>
          <button
            onClick={onClose}
            className="popup__close" type="button"></button>
          <h2 className={`popup__title popup__title_type_${name}`}>{title}</h2>
          <form
            onSubmit={submit}
            className={`popup__form popup__form_type_${name}`}
            name={`${name}-form`}
            noValidate
          >
            <>{children}</>
            <button
              className={`popup__submit-button popup__submit-button_type_${name}`}
              type="submit"
              onClick={onSubmit}
            >
              {buttonTitle}
            </button>
          </form>
        </div>
      </div>
  );
}

export default PopupWithForm;
